import { Component } from 'react';
import PropTypes from 'prop-types';

class Pokemon extends Component {
  render() {
    const { name, type, avarageWeight, image } = this.props.pokemon;

    return (
      <div>
        <p>{name}</p>
        <p>{type}</p>
        <p>{`Average weight: ${avarageWeight}`}</p>
        <img src={image} />
      </div>
    );
  }
}

