import React, { Component } from 'react';
import Name from './Name';
import Email from './Email'
import Cpf from './Cpf'
import Adress from './Adress'

class PersonalData extends Component {
  constructor() {
    super();

    this.handleChange = this.handleChange.bind(this);

    this.state = {
      name: '',
      email: '',
      cpf: 0,
      adress: '',
    }
  }

  handleChange({ target }) {
    const { name, value } = target;
    // const value = target.name === 'name' ? target.value.toUpperCase() : target.value;

    if (value.name === 'name') {
      console.log("entrou")
      return value = value.toUpperCase();
    }

    if(tname === 'adress') {
      target.value.replace(/[^a-z0-9]/gi,'');
    }

    this.setState({
      [name]: value,
    });
  }

  render() {
    return (
      <fieldset>
        <legend>Dados Pessoais</legend>
        <Name value={this.state.name} handleChange={this.handleChange} />
        <Email value={this.state.email} handleChange={this.handleChange} />
        <Cpf value={this.state.cpf} handleChange={this.handleChange} />
        <Adress value={this.state.adress} handleChange={this.handleChange} />
      </fieldset>
    );
  }
}

export default PersonalData;
