import React, { Component } from 'react';

class Tipo extends Component {
  render() {

    const { value, handleChange } = this.props;

    return (
      <label>
        Tipo:
        <input
          type="radio"
          name="tipo"
          value=""
          onChange={handleChange}
          required
       />Casa
       <input
         type="radio"
         name="tipo"
         value={value}
         onChange={handleChange}
         required
       />Apartamento
      </label>

    );
  }
}

export default Tipo;
