import React, { Component } from 'react';
import Abstract from './Abstract';
import Cargo from './Cargo';


class LastJob extends Component {
  constructor() {
    super();

    this.handleChange = this.handleChange.bind(this);
    this.handleBlur = this.handleMouse.bind(this);

    this.state = {
      abstract: '',
      cargo: '',
      description: '',
    }
  }

  handleChange({ target }) {
    const { name, value } = target;

    this.setState({
      [name]: value,
    });
  }

  handleMouse(event) {
    if ((event.target.value).match(/^\d/)) {
      event.target.value = (event.target.value).replace(/^\d/, '');
    }

    this.handleChange(event);
  }

  render() {
    return (
      <fieldset>
        <legend>Dados do Último Emprego</legend>
        <Abstract value={this.state.abstract} handleChange={this.handleChange} />
        <Cargo value={this.state.cargo} handleChange={this.handleChange} />
      </fieldset>
    );
  }
}

export default LastJob;
